
public class J_S1A2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=20;
int b=10;

System.out.print("Initial value of a is "+a+" & b is "+b);
System.out.print("\n");

b= a-- - --a;
int c=a--;
int d=a>>2;
int e=a&b;

System.out.print("\n");
System.out.print("Value of (b=a-- - --a) is "+b);
System.out.print("\n");
System.out.print("Value of (c=a--) is "+c);
System.out.print("\n");
System.out.print("Value of (d=a>>2) is "+d);
System.out.print("\n");
System.out.print("Value of (e=a & b) is "+e);
	}

}
